<?php

namespace Appstore\Bundle\EcommerceBundle\Repository;
use Doctrine\ORM\EntityRepository;

/**
 * TemplateRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TimePeriodRepository extends EntityRepository
{
    public function getApiTimePeriod($config)
    {
        $qb = $this->createQueryBuilder('e');
        $qb->select('e.id as id','e.name as name','e.name as nameBn');
        $qb->where("e.ecommerceConfig = :option")->setParameter('option', $config->getId());
        $result = $qb->getQuery()->getArrayResult();
        return $result;

    }
}
