<?php

namespace Appstore\Bundle\ElectionBundle\Repository;
use Appstore\Bundle\ElectionBundle\Entity\ElectionCandidate;
use Doctrine\ORM\EntityRepository;

/**
 * CustomerRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ElectionCandidateRepository extends EntityRepository
{
    public function updateTotalVote(ElectionCandidate $entity,$data)
    {
    	$entity->setMaleVote($data['maleVoter']);
    	$entity->setFemaleVote($data['femaleVoter']);
    	$entity->setOtherVote($data['otherVoter']);
    	$entity->setTotalVote($data['totalVoter']);
	    $this->_em->persist($entity);
	    $this->_em->flush();
	    return $entity;
    }


}
