<?php

namespace Appstore\Bundle\InventoryBundle\Repository;

use Appstore\Bundle\InventoryBundle\Entity\ItemGallery;
use Doctrine\ORM\EntityRepository;

/**
 * ProductGalleryRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ItemGalleryRepository extends EntityRepository
{
    public function insertProductGallery($reEntity,$data)
    {

        $em = $this->_em;
        $i=0;

        foreach ($data as $key => $value) {
            $entity = new ItemGallery();
            if(strpos($key,'tmpname')){
                $imageName = nl2br(htmlentities(stripslashes($value)));
                $entity->setPath($imageName);
                $entity->setPurchaseVendorItem($reEntity);
                $em->persist($entity);

            }
            $i++;

        }
        $em->flush();

        if(!empty($data['imageId'])){
            $this->removeImage($data['imageId']);
        }
    }

    public function removeImage($posts)
    {
        $em = $this->_em;
        foreach ($posts as $post ){
            $entity = $em->getRepository('InventoryBundle:ItemGallery')->find($post);
            $entity->removeUpload($entity->getPurchaseVendorItem()->getInventoryConfig()->getGlobalOption()->getId(),$entity->getPurchaseVendorItem()->getId());
            $em->remove($entity);
        }
        $em->flush();


    }

}
