<?php

namespace Appstore\Bundle\MedicineBundle\Repository;


use Appstore\Bundle\MedicineBundle\Entity\DiagnosticReport;
use Doctrine\ORM\EntityRepository;

/**
 * MedicineCompanyRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */


class DiagnosticReportRepository extends EntityRepository
{

    public function getInvestigationGroupList()
    {
        $qb = $this->createQueryBuilder('e');
        $qb->select('e');
        $qb->join('e.category', 'c');
        $qb->orderBy('c.name', 'ASC');
        $qb->andWhere('e.status =:status')->setParameter('status',1);
        $products = $qb->getQuery()->execute();
        $choices = [];
        /* @var $product DiagnosticReport */
        foreach ($products as $product) {
            $choices[$product->getCategory()->getName()][$product->getId()] =  $product->getName();
        }
        return $choices;

    }

}
