<?php

namespace Product\Bundle\ProductBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * ProductReviewRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductReviewRepository extends EntityRepository
{
    public function addReview($data,$product)
    {

        $em = $this->_em;
        $entity = new ProductReview();

        $entity->setProduct($product);
        $entity->setName($data['name']);
        $entity->setMobile($data['mobile']);
        $entity->setEmail($data['email']);
        $entity->setComment($data['review']);
        $entity->setRating($data['rating']);
        $em->persist($entity);
        $em->flush();



    }
}
