<?php

namespace Setting\Bundle\AppearanceBundle\Repository;
use Doctrine\ORM\EntityRepository;
use Setting\Bundle\ToolBundle\Entity\GlobalOption;

/**
 * SidebarWidgetPanelRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SidebarWidgetPanelRepository extends EntityRepository
{
    public function getSidebarPanel(GlobalOption $option,$sidebarWidget)
    {
        $em = $this->_em;
        $entity = $em->getRepository('SettingAppearanceBundle:SidebarWidgetPanel')->findOneBy(array('globalOption' => $option,'sidebarWidget' => $sidebarWidget));
        return $entity;
    }
}
