<?php

namespace Setting\Bundle\ContentBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * TestimonialRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TestimonialRepository extends EntityRepository
{
    public function  getRandomTestimonial()
    {
        $count = 2;
        $em = $this->_em;
        $entities = $em->getRepository('SettingContentBundle:Testimonial')->findBy(array('status' => 1));
        if (!empty($entities)){
            return $data = $this->createQueryBuilder('t')
                ->where('t.status = :status')
                ->setFirstResult(rand(0, $count - 1))
                ->setMaxResults(1)
                ->setParameter('status', true)
                ->getQuery()
                ->getSingleResult();
        }

    }

    public function findFeatureContent($entity)
    {
        $user = $entity->getUser()->getId();
        $limit = $entity->getUser()->getHomePage()->getShowingListing();
        return $this->getEntityManager()
            ->createQuery('SELECT n FROM SettingContentBundle:Testimonial n WHERE n.status = 1 AND n.user ='.$user.' ORDER BY n.created DESC')
            ->setMaxResults($limit)
            ->getResult();
    }

    public function findUserModuleContent($entity)
    {
        $user = $entity->getUser()->getId();
        $limit = 5;
        $currDate = date('Y-m-d h:m:s');
        $qb = $this->createQueryBuilder('a');
        $qb
            ->where($qb->expr()->eq('a.status', 1))
            ->andWhere($qb->expr()->eq('a.user', $user))
            ->setMaxResults($limit)
            ->orderBy('a.created', 'DESC');

        return $qb->getQuery()->getResult();
    }

}
