<?php

namespace Setting\Bundle\ToolBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * BrandingRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BrandingRepository extends EntityRepository
{
    public function getBrandingSponsor()
    {

        $em = $this->_em;
        $entity = $em->getRepository('SettingToolBundle:Branding')->findBy(array('status'=>1,'sponsor'=>1));
        if(!empty($entity)){

            return $entity;

        }
    }

    public function getCategoryBrand($categoryArr)
    {

        $arr = implode(',',$categoryArr);

        $em = $this->_em;
        $connection = $em->getConnection();
        $statement = $connection->prepare("SELECT distinct b.name,b.id FROM branding as b
         LEFT OUTER JOIN branding_category as bc
         ON b.id = bc.branding_id
         WHERE b.status = :status AND bc.category_id in ($arr) ");
        $statement->bindValue('status', 1);
        $statement->execute();
        return $results = $statement->fetchAll();

    }

    public function searchAutoComplete($q)
    {
        $query = $this->createQueryBuilder('e');
        $query->select('e.name as id');
        $query->addSelect('e.name as text');
        $query->where($query->expr()->like("e.name", "'$q%'"  ));
        $query->groupBy('e.id');
        $query->orderBy('e.name', 'ASC');
        $query->setMaxResults( '10' );
        return $query->getQuery()->getResult();

    }

}
